mapper.select:
SELECT SEQ, ID, TITLE, CONTENT, HASHTAG, WDATE, SHOPNAME, ADDRESS, READCNT, LIKECNT
FROM EATAEWONBBS
WHERE SEQ = #{seq}

mapper.update:
UPDATE EATAEWONBBS
SET READCNT=READCNT+1
WHERE SEQ=#{seq}

mapper.select:
SELECT COUNT(*)
FROM EATAEWONLIKE
WHERE BBSSEQ = #{bbsseq} AND ID = #{id}

SELECT COUNT(*)
FROM EATAEWONSCRAP
WHERE BBSSEQ = #{bbsseq} AND ID = #{id}
