mapper.select:
SELECT NVL(COUNT(*),0)
FROM EATAEWONMEMBER
WHERE ID=#{id}

SELECT ID
FROM EATAEWONMEMBER
WHERE EMAIL=#{email}

mapper.update:
UPDATE EATAEWONMEMBER
SET PWD = #{pwd}
WHERE ID = #{id}

UPDATE EATAEWONMEMBER
SET LIKEPOINT = LIKEPOINT+50
WHERE ID = #{id}

UPDATE EATAEWONMEMBER
SET LIKEPOINT = LIKEPOINT+1
WHERE ID = #{id}

UPDATE EATAEWONMEMBER
SET LIKEPOINT = LIKEPOINT-1
WHERE ID = #{id}

UPDATE EATAEWONMEMBER
SET LIKEPOINT = LIKEPOINT+5
WHERE ID = #{id}

UPDATE EATAEWONMEMBER
SET LIKEPOINT = LIKEPOINT-5
WHERE ID = #{id}
