mapper.update:
UPDATE EATAEWONBBS
SET READCNT=READCNT+1
WHERE SEQ=#{seq}

mapper.select:
SELECT COUNT(*)
FROM EATAEWONLIKE
WHERE BBSSEQ = #{bbsseq} AND ID = #{id}

SELECT COUNT(*)
FROM EATAEWONSCRAP
WHERE BBSSEQ = #{bbsseq} AND ID = #{id}

mapper.update:
update EATAEWONBBS
SET likecnt = likecnt+1
WHERE seq = #{seq}

update EATAEWONBBS
SET likecnt = likecnt-1
WHERE seq = #{seq}
