mapper.select:
SELECT NVL(COUNT(*),0)
FROM EATAEWONMEMBER
WHERE ID=#{id}

mapper.update:
update EATAEWONMEMBER
SET LIKEPOINT = LIKEPOINT+50
WHERE ID = #{id}

update EATAEWONMEMBER
SET LIKEPOINT = LIKEPOINT+1
WHERE ID = #{id}

update EATAEWONMEMBER
SET LIKEPOINT = LIKEPOINT-1
WHERE ID = #{id}

update EATAEWONMEMBER
SET LIKEPOINT = LIKEPOINT+5
WHERE ID = #{id}

update EATAEWONMEMBER
SET LIKEPOINT = LIKEPOINT-5
WHERE ID = #{id}
