mapper.select:
SELECT SEQ, Nickname, ID, TITLE, CONTENT, PICTURE, HASHTAG ,WDATE, SHOPNAME, ADDRESS, READCNT, LIKECNT,shopphnum,shopurl
FROM EATAEWONBBS
WHERE SEQ=#{seq}

mapper.update:
UPDATE EATAEWONBBS
SET READCNT=READCNT+1
WHERE SEQ=#{seq}

UPDATE EATAEWONBBS
SET TITLE=#{title}, SHOPNAME=#{shopname},CONTENT=#{content}, PICTURE=#{picture}, HASHTAG=#{hashtag},
LATITUDE=#{latitude}, LONGTITUDE=#{longtitude},ADDRESS=#{address}, shopphnum=#{shopphnum}, shopurl=#{shopurl}
WHERE SEQ=#{seq}

mapper.select:
SELECT COUNT(*) AS CNT
FROM EATAEWONSCRAP
WHERE bbsseq = #{bbsseq}
AND ID = #{id}

mapper.delete:
DELETE FROM EATAEWONSCRAP
WHERE BBSSEQ=#{bbsseq}
  AND ID = #{id}

mapper.select:
SELECT COUNT(*) AS CNT
  FROM EATAEWONLIKE
WHERE bbsseq = #{bbsseq}
AND ID = #{id}

mapper.update:
update EATAEWONMEMBER
SET likepoint = likepoint+50
WHERE id = #{id}

update EATAEWONMEMBER
SET likepoint = likepoint-50
WHERE id = #{id}

update EATAEWONBBS
SET likecnt = likecnt+1
WHERE seq = #{bbsseq}

update EATAEWONBBS
SET likecnt = likecnt-1
WHERE seq = #{bbsseq}

mapper.delete:
DELETE FROM EATAEWONLike
 WHERE BBSSEQ=#{bbsseq}
   AND ID = #{id}

