mapper.select:
SELECT SEQ, ID, TITLE, CONTENT, HASHTAG ,WDATE, SHOPNAME, ADDRESS, READCNT, LIKECNT
FROM EATAEWONBBS
WHERE SEQ=#{seq}

mapper.update:
UPDATE EATAEWONBBS
SET READCNT=READCNT+1
WHERE SEQ=#{seq}

UPDATE EATAEWONBBS
SET TITLE=#{title}, CONTENT=#{content}, SHOPNAME=#{shopname}, HASHTAG=#{hashtag}, ADDRESS=#{address},
    LATITUDE=#{latitude}, LONGTITUDE=#{longtitude}
WHERE SEQ=#{seq}
